import React, { Component } from 'react';
import './Members.css';
import {members} from './data';
import Button from 'react-bootstrap/Button';
import pic from './shibayan.jpeg';

export default class Members extends Component {
    render() {
        const professors = members.professors.map(mem => {
            const con = mem.contact
            return (
              <section className="memberInfoCard">
                <div className='memImage'>
                  {/* <img src={require(`../../../Images/people/${mem.userId}.${mem.imageFormat}`)} alt={mem.userId} data-position="center center"></img> */}
                  <img src={pic} alt={mem.userId} data-position="center center"></img>
                </div>
                <div className='content'>
                  <h2>{mem.name}</h2>
                  <h4>{mem.role}</h4>
                  <h5>{mem.department}</h5>
                  <p></p>
                    <div class="btn-group" role="group" aria-label="Basic example">
                    <Button variant="light">Light</Button>
                    <Button variant="light">Light</Button>
                    <Button variant="light">Light</Button>
                    </div>
                </div>
              </section>
            )
          }) 
        return (
            <div id="membersBaseDiv">
                <p class="MembersHeader">Faculty</p>
                {professors}
                <p class="MembersHeader">Current Members</p>
                <p class="MembersHeader">Our Alumni</p>
            </div>
        )
    }
}
